closeButton": true,            "debug": true,            "newestOnTop": true,            "progressBar": true,            "positionClass": "toast-bottom-right animated-panel fadeInTop",            "preventDuplicates": true,            "onclick": null,            "showDuration": "300",            "hideDuration": duration,            "timeOut": "5000",            "extendedTimeOut": "1000",            "showEasing": "swing",            "hideEasing": "linear",            "showMethod": "fadeIn",            "hideMethod": "fadeOut"        }    }    </script><!-- Global site tag (gtag.js) - Google Analytics --><script async src="https://www.googletagmanager.com/gtag/js?id=UA-147436717-1"></script><script>    window.dataLayer = window.dataLayer || [];    function gtag(){dataLayer.push(arguments);}    gtag('js', new Date());    gtag('config', 'UA-147436717-1');</script></body></html>